var allMobs = Entity.getAll();

function removeAllMobs(){
	for(var i = 0; i < allMobs.length; i++){
		Entity.remove(allMobs[i]);
	}
}
function killAllMobs(){
	for(var i = 0; i < allMobs.length; i++){
		Entity.setHealth(allMobs[i], 0);
	}
}



