	function writeFileFromByteArray(byteArray, Path)
{
	var fontFile = new java.io.File(Path);
	if(fontFile.exists())
		fontFile.delete();
	fontFile.createNewFile();
	var fontStream = new java.io.FileOutputStream(fontFile);
	fontStream.write(byteArray);
	fontStream.close();
}

 

