<!DOCTYPE html> <html> <head> <meta charset="UTF-8"> <title>メール送信完了｜メール送信フォーム</title> </head> <body> <?php /******************************* データの受け取り *******************************/ $namae		= $_POST["namae"];		//お名前 $mailaddress	= $_POST["mailaddress"]; $naiyou		= $_POST["naiyou"];	 $namae		= htmlspecialchars($namae, ENT_QUOTES); $mailaddress	= htmlspecialchars($mailaddress, ENT_QUOTES); $naiyou		= htmlspecialchars($naiyou, ENT_QUOTES); /******************************* 未入力チェック *******************************/ $errmsg = ''; if ($namae == '') { 	$errmsg = $errmsg.'<p>お名前が入力されていません。</p>'; } if ($mailaddress == '') { 	$errmsg = $errmsg.'<p>メールアドレスが入力されていません。</p>'; } if ($naiyou == '') { 	$errmsg = $errmsg.'<p>お問合せ内容が入力されていません。</p>'; } if ($errmsg != '') { 	echo $errmsg; 	 	echo '<form method="post" action="index.php">'; 	echo '<input type="hidden" name="namae" value="'.$namae.'">'; 	echo '<input type="hidden" name="mailaddress" value="'.$mailaddress.'">'; 	echo '<input type="hidden" name="naiyou" value="'.$naiyou.'">'; 	echo '<input type="submit" name="backbtn" value="前のページへ戻る">'; 	echo '</form>'; } else { 	 	$honbun = ''; 	$honbun .= "メールフォームよりお問い合わせがありました。\n\n"; 	$honbun .= "【お名前】\n"; 	$honbun .= $namae."\n\n"; 	$honbun .= "【メールアドレス】\n"; 	$honbun .= $mailaddress."\n\n"; 	$honbun .= "【お問い合わせ内容】\n"; 	$honbun .= $naiyou."\n\n"; 	 	 	mb_language("Japanese"); 	mb_internal_encoding("UTF-8"); 	 	$mail_to	= "●●●●●●●";	 	$mail_subject	= "メールフォームよりお問い合わせ";	 	$mail_body	= $honbun;		 	$mail_header	= "from:".$mailaddress;	 	$mailsousin	= mb_send_mail($mail_to, $mail_subject, $mail_body, $mail_header); 	 	 	if($mailsousin == true) { 		echo '<p>お問い合わせメールを送信しました。</p>'; 	} else { 		echo '<p>メール送信でエラーが発生しました。</p>'; 	} } ?> </body> </html>
